con:config.script:
def firstSet = testRunner.testCase.getPropertyValue("FirstSet").split(",")
def secondSet = testRunner.testCase.getPropertyValue("SecondSet").split(",")
def results = testRunner.testCase.getPropertyValue("Results").split(",")
def i = Integer.parseInt(testRunner.testCase.getPropertyValue("Counter"))

def a = firstSet[i]
def b = secondSet[i]
def result = results[i]
i++

log.info "Multiply. Calculation dataSet " + i.toString() + " (" + a.toString() + " ;" + b.toString() + "; " + result.toString()+ ")" 

testRunner.testCase.setPropertyValue("A", a.toString())
testRunner.testCase.setPropertyValue("B", b.toString())
testRunner.testCase.setPropertyValue("Result", result.toString())
testRunner.testCase.setPropertyValue("Counter", i.toString())

